import React, { useState, useEffect } from 'react';
import { useTheme } from '../context/ThemeContext';
import './Navbar.css';

function Navbar() {
  const { isDarkMode, toggleTheme } = useTheme();
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      const offset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - offset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
      setIsMenuOpen(false);
    }
  };

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''} ${isMenuOpen ? 'menu-open' : ''}`}>
      <div className="navbar-content">
        <div className="navbar-logo" onClick={() => scrollToSection('hero')}>
          <div className="logo-container">
            <span className="logo-text">PC</span>
            <span className="logo-full">Paul COHEN</span>
          </div>
        </div>

        <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
          {[
            { id: 'about', label: '√Ä propos', icon: 'üë§' },
            { id: 'skills', label: 'Comp√©tences', icon: 'üíª' },
            { id: 'exams', label: 'BTS SIO', icon: 'üìö' },
            { id: 'projects', label: 'Projets', icon: 'üöÄ' },
            { id: 'contact', label: 'Contact', icon: 'üìß' }
          ].map(item => (
            <li key={item.id}>
              <button
                className={`nav-button ${activeSection === item.id ? 'active' : ''}`}
                onClick={() => scrollToSection(item.id)}
              >
                <span className="button-icon">{item.icon}</span>
                <span className="button-text">{item.label}</span>
                {activeSection === item.id && <span className="active-indicator" />}
              </button>
            </li>
          ))}
        </ul>

        <div className="navbar-actions">
          <button 
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label={isDarkMode ? 'Activer le mode clair' : 'Activer le mode sombre'}
          >
            {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>

        <div className="navbar-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
          <div className="toggle-icon">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
