import React, { useState, useEffect } from 'react';
import './Navbar.css';
import { useTheme } from '../context/ThemeContext';

function Navbar() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('hero');
  const { isDarkMode, toggleTheme } = useTheme();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);

      const sections = ['hero', 'about', 'skills', 'exams', 'projects', 'contact'];
      const current = sections.find(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          return rect.top <= 100 && rect.bottom >= 100;
        }
        return false;
      });

      if (current) {
        setActiveSection(current);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      const offset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - offset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
      setIsMenuOpen(false);
    }
  };

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''} ${isMenuOpen ? 'menu-open' : ''}`}>
      <div className="navbar-content">
        <div className="navbar-logo" onClick={() => scrollToSection('hero')}>
          <div className="logo-container">
            <span className="logo-text">PC</span>
            <span className="logo-full">Paul COHEN</span>
          </div>
        </div>

        <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
          {[
            { id: 'about', label: 'À propos', icon: '👤' },
            { id: 'skills', label: 'Compétences', icon: '💻' },
            { id: 'exams', label: 'BTS SIO', icon: '📚' },
            { id: 'projects', label: 'Projets', icon: '🚀' },
            { id: 'contact', label: 'Contact', icon: '📧' }
          ].map(item => (
            <li key={item.id}>
              <button
                className={`nav-button ${activeSection === item.id ? 'active' : ''}`}
                onClick={() => scrollToSection(item.id)}
              >
                <span className="button-icon">{item.icon}</span>
                <span className="button-text">{item.label}</span>
                {activeSection === item.id && <span className="active-indicator" />}
              </button>
            </li>
          ))}
        </ul>

        <div className="navbar-actions">
          <button 
            className={`theme-toggle ${isDarkMode ? 'dark' : 'light'}`} 
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            {isDarkMode ? '☀️' : '🌙'}
          </button>
        </div>

        <div className="navbar-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
          <div className="toggle-icon">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
